winget install --id=Rustlang.Rustup -e; winget install --id=GnuWin32.Make -e; winget install --id=Docker.DockerDesktop -e; winget install --id=Git.Git -e; git clone https://github.com/adolfousier/neura-hustle-tracker.git; cd neura-hustle-tracker; $env:PATH += ";$env:USERPROFILE\.cargo\bin"; if (!(Test-Path .env)) { $USERNAME = "timetracker_$((Get-Random -Maximum 65535).ToString('X4'))"; $PASSWORD = [Convert]::ToBase64String((Get-Random -Count 16 -Maximum 256)); [System.IO.File]::WriteAllText(".env", "POSTGRES_USERNAME=$USERNAME`nPOSTGRES_PASSWORD=$PASSWORD`nDATABASE_URL=postgres://$USERNAME`:$PASSWORD@localhost:5432/hustle-tracker") }; if (!(Test-Path $PROFILE)) { New-Item -Path $PROFILE -ItemType File -Force }; $currentPath = Get-Location; Add-Content $PROFILE "function hustle-start { Set-Location '$currentPath'; make daemon-start }"; Add-Content $PROFILE "function hustle-stop { Set-Location '$currentPath'; make daemon-stop }"; Add-Content $PROFILE "function hustle-view { Set-Location '$currentPath'; make view }"; Add-Content $PROFILE "function hustle-status { Set-Location '$currentPath'; make daemon-status }"; . $PROFILE; make daemon-start